import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CardList from './CardList';
import Typography from '@material-ui/core/Typography';
import NotInterestedIcon from '@material-ui/icons/NotInterested';
import { Grid } from '@material-ui/core';
import {ReactComponent as Rec} from '../assets/recommend.svg';

const useStyles = makeStyles((theme) => ({
    root: {
        width:'85%',
        padding: theme.spacing(2)
    },
    title:{
        margin: theme.spacing(2),
    },
  }));

function Reccomendations({trainers}) {
    const app = useContext(AppContext);
    const classes = useStyles();
    const pref = app.state.pref;
    const arr = pref.split(",");
    console.log(arr)
    const robots = trainers.filter(robot=>{return robot.specialization.toLowerCase().includes('')});

    return (
        <Grid container justify="center">
        <Typography className={classes.title} variant="h4">Your Reccomendations based on your preference</Typography>
        <Rec />
        <div className={classes.root}>
        {
            robots.length?
            (<CardList robots={robots} />)
            :
            (<Grid container justify="center">
            <NotInterestedIcon />
            <Typography variant="subtitle1">No results</Typography>
            </Grid>)
        }
        </div>
        </Grid>
    )
}

export default Reccomendations;
